"use strict";var express=require("express"),Ingredients=require("./ingredient-model.js"),router=express.Router();router.get("/:id/recipes",function(e,s){var t=e.params.id;Ingredients.getRecipesWithIngredient(t).then(function(e){e.length?s.status(200).json(e):s.status(404).json({message:"Could not find recipe for given ingredient"})}).catch(function(e){s.status(500).json({message:"Failed to get recipes"})})});